", "
", "# Plotting the measurements data
", "plt.figure()
", "plt.plot(t, x_gt[:, 0], 'b', label='position ground-truth', alpha=0.5)
", "plt.plot(t, x_noisy[:, 0], 'b--', label='position measurement')
