", " if (event.altKey && event.which != 18)
", " value += "alt+";
", " if (event.shiftKey && event.which != 16)
", " value += "shift+";
", "
", " value += 'k';
", " value += event.which.toString();
", "
", " this._key_event_extra(event, name);
", "
", " this.send_message(name, {key: value,
", " guiEvent: simpleKeys(event)});
", " return false;
", "}
