", "mpl.figure = function(figure_id, websocket, ondownload, parent_element) {
", " this.id = figure_id;
", "
", " this.ws = websocket;
", "
", " this.supports_binary = (this.ws.binaryType != undefined);
", "
", " if (!this.supports_binary) {
