", "mpl.figure.prototype.handle_cursor = function(fig, msg) {
", " var cursor = msg['cursor'];
", " switch(cursor)
", " {
", " case 0:
", " cursor = 'pointer';
", " break;
", " case 1:
", " cursor = 'default';
", " break;
", " case 2:
", " cursor = 'crosshair';
", " break;
", " case 3:
", " cursor = 'move';
", " break;
", " }
", " fig.rubberband_canvas.style.cursor = cursor;
", "}
