", "mpl.get_websocket_type = function() {
", " if (typeof(WebSocket) !== 'undefined') {
", " return WebSocket;
", " } else if (typeof(MozWebSocket) !== 'undefined') {
", " return MozWebSocket;
", " } else {
", " alert('Your browser does not have WebSocket support. ' +
