", " start: function(event, ui) {
", " pass_mouse_events = false;
", " },
", " resize: function(event, ui) {
", " fig.request_resize(ui.size.width, ui.size.height);
", " },
", " stop: function(event, ui) {
", " pass_mouse_events = true;
", " fig.request_resize(ui.size.width, ui.size.height);
", " },
", " });
