", " icon_img.addClass(image);
", " icon_img.addClass('ui-corner-all');
", "
", " var tooltip_span = $('');
", " tooltip_span.addClass('ui-button-text');
", " tooltip_span.html(tooltip);
", "
", " button.append(icon_img);
", " button.append(tooltip_span);
", "
", " nav_element.append(button);
", " }
