", "mpl.figure.prototype.handle_resize = function(fig, msg) {
", " var size = msg['size'];
", " if (size[0] != fig.canvas.width || size[1] != fig.canvas.height) {
", " fig._resize_canvas(size[0], size[1]);
", " fig.send_message("refresh", {});
